class Solution:    def average(self, salary: List[int]) -> float:        #print(sum(salary))        #Τ炒馈A{Α临ぃ癸Ag2妗A单@U|抓        total = sum(salary)-max(salary)-min(salary) #[`-程j-程p        N = len(salary)-2 #]唉┍汲踏j取B程p取A计ヘ]Ι奔2        return total / N        #return (sum(salary)-max(salary)-min(salary))/(len(salary)-2)